  }) */

  const handleSingleSelectChange = React.useCallback((e) => {

    setOption(list_options[e.target.value-1]);

  }, []);
  
  return !isMulti ? (
    <div>
      <div
        className="custom-select"
      >
        {data.map((options) => {
          return (
            <>
              <input
              type="radio"
              value={options.id}
              onClick={handleSingleSelectChange}
              checked={options.id === option?.id}
              />
              {options.name}
            </> 
          )
        })}
      </div>
    </div>
  ) : (
    <div>
      <div
        className="custom-select"
      >
        {data.map((options) => {
          return (
            <>
              <input
              type="checkbox"
              value={options.id}
              onClick={onSelect}
              />
              {options.name}
            </>
          )
        })}
      </div>
    </div>
  );
};

export default DropdownComponent;
