import React, { useState, useCallback } from "react";

const DropdownComponent = (props) => {
  const { isMulti } = props;
  const [option, selectOption] = useState("one");

  const handleSingleSelectChange = useCallback(
    (e) => {
      selectOption(e.target.value);
    },
    []
  );
  const handleMultiSelectChange = useCallback(
    (e) => {
      selectOption(e.target.value);
    },
    []
  );

  if (isMulti) {
    return (
      <div>
        <form>
          <label>
            Pick your choice here:
            <select value={option} onChange={handleSingleSelectChange}>
              <option value="one">One</option>
              <option value="two">Two</option>
              <option value="three">Three</option>
            </select>
          </label>
        </form>
      </div>
    );
  } else {
    return (
      <div>
        <form>
          <label>
            Pick your choice here:
            <select value={option} onChange={handleMultiSelectChange}>
              <option value="one">One</option>
              <option value="two">Two</option>
              <option value="three">Three</option>
            </select>
          </label>
        </form>
      </div>
    );
  }
};

export default DropdownComponent;
