import React, { useState, useCallback } from "react";

const DropdownComponent = (props) => {
  const list_options = [
    {
      id: 1,
      name: "One",
    },
    {
      id: 2,
      name: "Two",
    },
    {
      id: 3,
      name: "Three",
    },
    {
      id: 4,
      name: "Four",
    },
    {
      id: 5,
      name: "Five",
    },
  ];
  const { isMulti } = props;
  const [option, selectOption] = useState(list_options[0]);
  const [isChecked, setIsChecked] = useState(new Array(list_options.length).fill(false));
  /* const [selectResult, setSelectResult] = useState(""); */
  /*  const [checkedState, setCheckedState] = useState(new Array(list_options.length).fill(false));
 /*  const [selectResult, setSelectResult] = useState("");

  const handleSingleSelectChange = useCallback((e) => {
    selectOption(e.target.value);
  }, []);

  /* const handleMultiSelectChange = useCallback((e) => {
    selectOption(e.target.value);
  }, []); */

  /* const handleOnChange = (position) => {
    const updatedCheckedState = checkedState.map((item, index) =>
      index === position ? !item : item
    );
  setCheckedState(updatedCheckedState);
 */

  const handleSingleSelectChange = useCallback((e) => {
    selectOption(e.target.value);
  }, []);

   const handleMultiSelectChange = useCallback((itemid) => {
      setIsChecked(!isChecked)
   },isChecked)

  if (isMulti) {
    return (
      <div>
        <form>
          <label>
            Pick your choice here:
            <select value={option} onChange={handleSingleSelectChange}>
              {" "}
              {list_options.map((options) => (
                <option key={options.id} value={options.name}>
                  {" "}
                  {options.name}{" "}
                </option>
              ))}{" "}
            </select>{" "}
          </label>{" "}
        </form>{" "}
        <div> Your choice is: {option} </div>{" "}
      </div>
    );
  } else {
    return (
      <div>
        Pick your choice here:{" "}
        {list_options.map((options) => (
          <div>
            <input type="checkbox" key={options.id} value={options.name} check={isChecked} onSelect={handleMultiSelectChange} />
            {options.name}
          </div>
        ))}{" "}
        {/* <div>Above checkbox is {isChecked ? "checked" : "unchecked"}. </div>{" "} */}{" "}
      </div>
    );
  }
};

export default DropdownComponent;
