import React,{ useState, useCallback } from "react";
import Form from 'reactstrap'

const DropdownComponent = (props) => {
  const { isMulti } = props;
  const [option, selectOption] = useState("one");

  const handleSingleSelectChange = useCallback((e) => {
    selectOption(e.target.value);
  },[option]);

  const handleMultiSeclectChange = useCallback((e) => {
    var options = e.target.options;
    var value = [];
    for (var i = 0, l = options.length; i < l; i++) {
      if (options[i].selected) {
        value.push(options[i].value);
      }
    }
    this.setState({value: value});
  },[option]);

  if (isMulti) {
    return (
      <div>
        <form>
          <label>
            Pick your choice here:
            <select value={option} onChange={handleSelectChange}>
              <option value="one">One</option>
              <option value="two">Two</option>
              <option value="three">Three</option>
            </select>
          </label>
        </form>
      </div>
    );
  } else {
    return (
      <div>
 <Form.Group >
          <Form.Label>Form Label</Form.Label>
          <Form.Control
            as="select"
            multiple
            // value={formCatState}
            onChange={(event) => {
              let target = event.target
              console.log(target.selectedOptions);
            }}
          >
            <option>example cat 1</option>
            <option>Example cat 2</option>
            <option>Example cat 3</option>
            <option>Example cat 4</option>
          </Form.Control>
            <Form.Text muted> hold ctrl or command for multiple select</Form.Text>
        </Form.Group>
      </div>
    );
  }
};

export default DropdownComponent;
