import React from "react";
import "./DropdownComponent.css";

const DropdownComponent = (props) => {
  const { isMulti, data, onSelect } = props;
  /* const [option, setOption] = React.useState({}); */

  const [singleChoice, setSingleChoice] = React.useState(data[0]);
  const [listSelect, setListSelect] = React.useState([]);

  const handleSingleChoice = React.useCallback((e) => {
    console.log("hello2");
    setSingleChoice(data[e.target.value - 1]);
  });

  const handleMultiSelectChange = React.useCallback(
    (e) => {
      const index = listSelect.findIndex((item) => item?.id == e.target.value);

      if (index === -1) {
        setListSelect([...listSelect, list_options[e.target.value - 1]]);
      } else {
        setListSelect((list) => [
          ...list.filter((item) => item.id != e.target.value),
        ]);
      }
    },
    [listSelect]
  );

  const expanded = false;
  const showCheckboxes = React.useCallback((e) => {
    if (!expanded) {
      e.target.style.display = "block";
      expanded = true;
    } else {
      e.target.style.display = "none";
      expanded = false;
    }
  }, expanded ) 


  /* const handleSingleSelectChange = React.useCallback((e) => {
    console.log("hello")
    setOption(list_options[e.target.value - 1]);
  }, []); */

  return !isMulti ? (
    <div>
      <div className="box">
        <select value={singleChoice.id} onChange={handleSingleChoice}>
          {data.map((options) => {
            return (
              <option
                key={options.id}
                type="radio"
                value={options.id}
                onChange={onSelect}
              >
                {options.name}
              </option>
            );
          })}
        </select>
      </div>
      <div className="result-selection">
        Your selection: "{singleChoice.name}"
      </div>
    </div>
  ) : (
    <form>
      <div className="multiselect">
        <div className="selectBox" onclick={showCheckboxes}>
          <select>
            <option>Select an option</option>
          </select>
          <div className="overSelect"></div>
        </div>
        <div className="checkboxes">
          <label for="one">
            <input type="checkbox" id="one" />
            First checkbox
          </label>
          <label for="two">
            <input type="checkbox" id="two" />
            Second checkbox
          </label>
          <label for="three">
            <input type="checkbox" id="three" />
            Third checkbox
          </label>
        </div>
      </div>
    </form>
  );
};

export default DropdownComponent;
