import React from "react";
import "./DropdownComponent.css";

const DropdownComponent = (props) => {
  const { isMulti, data, onSelect } = props;
  /* const [option, setOption] = React.useState({}); */
  
  const [singleChoice, setSingleChoice] = React.useState({}); 

  const handleSingleChoice = React.useCallback((e) => {
    console.log("hello2")
    setSingleChoice(data[e.target.value - 1]);
  });

  /* const handleSingleSelectChange = React.useCallback((e) => {
    console.log("hello")
    setOption(list_options[e.target.value - 1]);
  }, []); */

  return !isMulti ? (
    <div>
      <div className="box">
        <select value={singleChoice.id} onChange={handleSingleChoice}>
          {data.map((options) => {
            return (
              <option
                key={options.id}
                type="radio"
                value={options.id}
                onChange={onSelect}
              >
                {options.name}
              </option>
            );
          })}
        </select>
        Your choice: {singleChoice.name}
      </div>
    </div>
  ) : (
    <div>
      <div style={{ marginLeft: "30px" }} className="custom-select">
        {data.map((options) => {
          return (
            <span key={options.id}>
              <input
                style={{ marginRight: "10px", marginLeft: "20px" }}
                type="checkbox"
                value={options.id}
                onChange={onSelect}
              />
              <span style={{ color: "#009688" }}>{options.name}</span>
            </span>
          );
        })}
      </div>
    </div>
  );
};

export default DropdownComponent;
