import React from "react";

const DropdownComponent = (props) => {
  const { isMulti, data, onSelect } = props;
  const [singleChoice, setSingleChoice] = React.useState();

  const handleSingleChoice = React.useCallback((e) => {
    setSingleChoice(data[e.target.value - 1]);
  });

  return !isMulti ? (
    <div>
      <select style={{ marginLeft: "30px" }} className="custom-select">
        {data.map((options) => {
          return (
            <option
              key={options.id}
              style={{ marginRight: "10px", marginLeft: "20px",color: "#009688"  }}
              type="radio"
              value={options.id}
              onClick={handleSingleChoice}
              onChange={onSelect}
              checked={options.id === singleChoice?.id}
            >
              {options.name}
            </option>
          );
        })}
      </select>
    </div>
  ) : (
    <div>
      <div style={{ marginLeft: "30px" }} className="custom-select">
        {data.map((options) => {
          return (
            <span key={options.id}>
              <input
                style={{ marginRight: "10px", marginLeft: "20px" }}
                type="checkbox"
                value={options.id}
                onClick={onSelect}
              />
              <span style={{ color: "#009688" }}>{options.name}</span>
            </span>
          );
        })}
      </div>
    </div>
  );
};

export default DropdownComponent;
