import './App.css';
import DropdownComponent from './components/DropdownComponent.js';
import React from "react";

const list_options = [
  {
    id: 1,
    name: "One",
  },
  {
    id: 2,
    name: "Two",
  },
  {
    id: 3,
    name: "Three",
  },
  {
    id: 4,
    name: "Four",
  },
  {
    id: 5,
    name: "Five",
  },
];

function App() {
  const [option, setOption] = React.useState();

  const [listSelect, setListSelect] = React.useState([]);

  const handleSingleSelectChange = React.useCallback((e) => {
    const selectedChoice = e.target.value;
    setOption(selectedChoice);
  }, []);

  const handleMultiSelectChange = React.useCallback(
    (e) => {
      const index = listSelect.findIndex((item) => item?.id === e.target.value);
      console.log(index);
      if (index === -1) {
        setListSelect([...listSelect, list_options[e.target.value - 1]]);
      } else {
        console.log([...listSelect.filter((item) => item.id !== e.target.value)])
        setListSelect((list) => [...list.filter((item) => item.id !== e.target.value)]);
      }
    },
    [listSelect]
  );

  return (
    <div className="App">
      <DropdownComponent isMulti={true} data={list_options} onSelect={handleMultiSelectChange}/>

      result:
      <div>
        {listSelect.map((choices) => {
          return <div key={choices.id}>
            {choices?.name}
          </div>;
        })}
      </div>
    </div>
  );
}

export default App;
