import React, { useState, useCallback } from "react";

const DropdownComponent = (props) => {
  const { isMulti } = props;
  const [option, selectOption] = useState("one");
  const [isCheckbox,setCheckbox] = useState("false")

  const handleSingleSelectChange = useCallback(
    (e) => {
      selectOption(e.target.value);
    },
    []
  );
  const handleMultiSelectChange = useCallback(
    (e) => {
      selectOption(e.target.value);
    },
    []
  );

  if (isMulti) {
    return (
      <div>
        <form>
          <label>
            Pick your choice here:
            <select value={option} onChange={handleSingleSelectChange}>
              <option value="one">One</option>
              <option value="two">Two</option>
              <option value="three">Three</option>
            </select>
          </label>
        </form>
      </div>
    );
  } else {
    return (
      <div>
      <div classname="topping">
            Pick your choice here:
            <input type="checkbox" id="topping" name="topping" value="One" checked={isChecked}
          onChange={handleOnChange}/>One
            <input type="checkbox" id="topping" name="topping" value="Two" checked={isChecked}
          onChange={handleOnChange} />Two
            <input type="checkbox" id="topping" name="topping" value="Three" checked={isChecked}
          onChange={handleOnChange} />Three
      </div>
      <div>
      Above checkbox is {isChecked ? "checked" : "unchecked"}.
      </div>
      </div>
    );
  }
};

export default DropdownComponent;
