import React from "react";
import "./DropdownComponent.css";

const DropdownComponent = (props) => {
  const { isMulti, data, onSelect } = props;
  /* const [option, setOption] = React.useState({}); */

  const [singleChoice, setSingleChoice] = React.useState(data[0]);
  const [listSelect, setListSelect] = React.useState([]);

  const handleSingleChoice = React.useCallback((e) => {
    console.log("hello2");
    setSingleChoice(data[e.target.value - 1]);
  });

  /* const handleMultiSelectChange = React.useCallback(
    (e) => {
      const index = listSelect.findIndex((item) => item?.id == e.target.value);

      if (index === -1) {
        setListSelect([...listSelect, list_options[e.target.value - 1]]);
      } else {
        setListSelect((list) => [
          ...list.filter((item) => item.id != e.target.value),
        ]);
      }
    },
    [listSelect]
  ); */

  return !isMulti ? (
    <div>
      <div className="box">
        <select value={singleChoice.id} onChange={handleSingleChoice}>
          {data.map((options) => {
            return (
              <option
                key={options.id}
                type="radio"
                value={options.id}
                onChange={onSelect}
              >
                {options.name}
              </option>
            );
          })}
        </select>
      </div>
      <div className="result-selection">
        Your selection: "{singleChoice.name}"
      </div>
    </div>
  ) : (
    <div>
      <div class="dropdown">
        <button class="dropbtn">Dropdown</button>
        <div class="dropdown-content">
          <a href="#">Link 1</a>
          <a href="#">Link 2</a>
          <a href="#">Link 3</a>
        </div>
      </div>
      <div className="box">
        <select value={singleChoice.id} onChange={handleSingleChoice}>
          {data.map((options) => {
            return (
              <option
                key={options.id}
                type="radio"
                value={options.id}
                onChange={onSelect}
              >
                {options.name}
              </option>
            );
          })}
        </select>
      </div>
      <div className="result-selection"></div>
    </div>
  );
};

export default DropdownComponent;
