import React from "react";

const DropdownComponent = (props) => {
  const { isMulti, data, onSelect } = props;
  return !isMulti ? (
    <div>
      <div
        className="custom-select"
      >
        {data.map((options) => {
          return (
            <>
              <input
              type="radio"
              value={options.name}
              onClick={onSelect}
              checked={options.name === options}
              />
              {options.name}
            </>
          )
        })}
      </div>
    </div>
  ) : (
    <div>
      Pick your choices here:
      <div
        className="custom-select"
      >
        {data.map((options) => {
          return (
            <>
              <input
              type="checkbox"
              value={options.id}
              onClick={onSelect}
              />
              {options.name}
            </>
          )
        })}
      </div>
    </div>
  );
};

export default DropdownComponent;
