import React from "react";

const DropdownComponent = (props) => {
  const { isMulti, data, onSelect } = props;
  const [singleChoice, setSingleChoice] = React.useState();

  const handleSingleChoice = React.useCallback((e) => {
    setSingleChoice(data[e.target.value - 1]);
  });

  return !isMulti ? (
    <div>
      <div style={{ marginLeft: "30px" }} className="custom-select">
        {data.map((options,index) => {
          return (
            <>
              <input
                style={{ marginRight: "10px", marginLeft: "20px" }}
                type="radio"
                value={options.id}
                onClick={handleSingleChoice}
                onChange={onSelect}
                checked={options.id === singleChoice?.id}
              />
              <span key={index} style={{ color: "#009688" }} key={index}>{options.name}</span>
            </>
          );
        })}
      </div>
    </div>
  ) : (
    <div>
      <div style={{ marginLeft: "30px" }} className="custom-select">
        {data.map((options,index) => {
          return (
            <>
              <input
                style={{ marginRight: "10px", marginLeft: "20px" }}
                type="checkbox"
                value={options.id}
                onClick={onSelect}
              />
              <span  style={{ color: "#009688" }} key={index}>{options.name}</span>
            </>
          );
        })}
      </div>
    </div>
  );
};

export default DropdownComponent;
