import "./App.css";
import DropdownComponent from "./components/DropdownComponent.js";
import React from "react";

const list_options = [
  {
    id: 1,
    name: "One",
  },
  {
    id: 2,
    name: "Two",
  },
  {
    id: 3,
    name: "Three",
  },
  {
    id: 4,
    name: "Four",
  },
  {
    id: 5,
    name: "Five",
  },
];

function App() {
  const [mode, setMode] = React.useState(true);
  const [option, setOption] = React.useState();

  const [listSelect, setListSelect] = React.useState([]);

  const handleSingleSelectChange = React.useCallback((e) => {
    const selectedChoice = e.target.value;
    setOption(selectedChoice);
  }, []);

  const handleMultiSelectChange = React.useCallback(
    (e) => {
      const index = listSelect.findIndex((item) => item?.id == e.target.value);

      if (index === -1) {
        setListSelect([...listSelect, list_options[e.target.value - 1]]);
      } else {
        setListSelect((list) => [
          ...list.filter((item) => item.id != e.target.value),
        ]);
      }
    },
    [listSelect]
  );

  const handleSubmit = useCallback((e) => {
    const choice_type = e.target.value;
    (choice_type === "single")? setMode(false) : setMode(true);
  },[])

  return (
    <div className="App">
      <div>
        Chon mode:
        <Form onSubmit={handleSubmit}>
          <Button value="single">Single Mode</Button>
          <Button value="multi">Multiple mode</Button>
        </Form>
      </div>
      {mode ? (
        <div>
          <DropdownComponent
            isMulti={mode}
            data={list_options}
            onSelect={handleMultiSelectChange}
          />
          <div>
            {listSelect.map((choices) => {
              return <div key={choices.id}>{choices?.name}</div>;
            })}
          </div>
        </div>
      ) : (
        <div>
          <DropdownComponent
            isMulti={mode}
            data={list_options}
            onSelect={handleSingleSelectChange}
          />
          result: {option}
        </div>
      )}
    </div>
  );
}

export default App;
