import React from "react";

const DropdownComponent = (props) => {
  
  /* const selectResult = new Array(list_options.length); */
  const { isMulti, data } = props;
  const [option, setOption] = React.useState();//xóa chỗ này pk
  
  const [listSelect, setListSelect] = React.useState([]);

  const handleSingleSelectChange = React.useCallback((e) => {
    const selectedChoice = e.target.value;
    setOption(selectedChoice);
  }, []);

  const handleMultiSelectChange = React.useCallback(
    (e) => {
      const index = listSelect.findIndex((item) => item?.id == e.target.value);
      if (index == -1) {
        setListSelect([...listSelect, list_options[e.target.value - 1]]);
      } else {
        setListSelect((list) => [...list.filter((item) => item.id != e.target.value)]);
      }
    },
    [listSelect]
  );

  return !isMulti ? (
    <div className="container p-5">
      <select
        className="custom-select"
        value={option}
        onChange={handleSingleSelectChange}
      >
        {list_options.map((options) => (
          <option key={options.id} value={options.name}>
            {options.name}
          </option>
        ))}
      </select>
      <br></br>
      Your choice is:  {option}
    </div>
  ) : (
    <div>
      Pick your choices here:
      <div
        className="custom-select"
      >
        {list_options.map((options) => {
          return (
            <>
              <input
              type="checkbox"
              value={options.id}
              onClick={handleMultiSelectChange}
              // checked={isChecked[options.id]}
              />
              {options.name}
            </>
          )
        })}
      </div>
      result:
      <div>
        {listSelect.map((choices) => {
          return <div>
            {choices?.name}
          </div>;
        })}
      </div>
    </div>
  );
};

export default DropdownComponent;
