import React from "react";

const DropdownComponent = (props) => {
  const { isMulti, data, onSelect } = prop
  return !isMulti ? (
    <div className="container p-5">
      {/* <select
        className="custom-select"
        value={option}
        onChange={onSelect}
      >
        {data.map((options) => (
          <option key={options.id} value={options.name}>
            {options.name}
          </option>
        ))}
      </select>
      <br></br> */}

    </div>
  ) : (
    <div>
      Pick your choices here:
      <div
        className="custom-select"
      >
        {data.map((options) => {
          return (
            <>
              <input
              type="checkbox"
              value={options.id}
              onClick={onSelect}
              // checked={isChecked[options.id]}
              />
              {options.name}
            </>
          )
        })}
      </div>
      
    </div>
  );
};

export default DropdownComponent;
