import "./App.css";
import React from "react";
import DropdownComponent from "./components/DropdownComponent.js";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

const list_options = [
  {
    id: 1,
    name: "One",
  },
  {
    id: 2,
    name: "Two",
  },
  {
    id: 3,
    name: "Three",
  },
  {
    id: 4,
    name: "Four",
  },
  {
    id: 5,
    name: "Five",
  },
];

function App() {
  const [mode, setMode] = React.useState();
  const [option, setOption] = React.useState({});
  const [listSelect, setListSelect] = React.useState([]);

  const handleSingleSelectChange = React.useCallback((e) => {
    setOption(list_options[e.target.value - 1]);
  }, []);

  const handleMultiSelectChange = React.useCallback(
    (e) => {
      const index = listSelect.findIndex((item) => item?.id == e.target.value);

      if (index === -1) {
        setListSelect([...listSelect, list_options[e.target.value - 1]]);
      } else {
        setListSelect((list) => [
          ...list.filter((item) => item.id != e.target.value),
        ]);
      }
    },
    [listSelect]
  );

  const handleClick = React.useCallback((e) => {
    e.target.value === "single" ? setMode(false) : setMode(true);
  }, []);

  return (
    <div
      style={{
        fontSize: "30px",
        fontFamily: "font-family: 'Noto Sans JP', sans-serif",
        margin: "5%",
      }}
    >
      <div>
        <img src="https://img.icons8.com/color/64/000000/hand-right.png" />
        <span style={{ fontSize: "50px", marginLeft: "5px" }}>You are in:</span>
        <div onClick={handleClick}>
          <button className="button-mode" value="single">
            Single Mode
          </button>
          <button className="button-mode" value="multi">
            Multiple mode
          </button>
        </div>
      </div>
      <div>
        <img src="https://img.icons8.com/color/48/000000/checkmark--v3.png" />
        <span>Pick your selection</span>
        {mode ? (
          <div>
            <DropdownComponent
              isMulti={mode}
              data={list_options}
              onSelect={handleMultiSelectChange}
            />
            <div>
              Result:
              {listSelect.map((choices) => {
                return <div key={choices.id}>{choices?.name}</div>;
              })}
            </div>
          </div>
        ) : (
          <div>
            <DropdownComponent
              isMulti={mode}
              data={list_options}
              onSelect={handleSingleSelectChange}
            />
            Result: {option.name}
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
