import React, { useState, useCallback } from "react";

const DropdownComponent = (props) => {

  const list_options = [
    { 
      id: 1, 
      name:"One"
    },
    { 
      id: 2, 
      name:"Two"
    },
    { 
      id: 3, 
      name:"Three"
    }
  ]
  const { isMulti } = props;
  const [option, selectOption] = useState("one");
  const [checkedState, setCheckedState] = useState(new Array(list_options.length).fill(false));
  const [selectResult, setSelectResult] = useState("");

  
  const handleSingleSelectChange = useCallback((e) => {
    selectOption(e.target.value);
  }, []);

  /* const handleMultiSelectChange = useCallback((e) => {
    selectOption(e.target.value);
  }, []); */

  /* const handleOnChange = (position) => {
    const updatedCheckedState = checkedState.map((item, index) =>
      index === position ? !item : item
    );
  setCheckedState(updatedCheckedState);
 */

  if (isMulti) {
    return (
      <div>
        <form>
          <label>
            Pick your choice here:
            <select onChange={handleSingleSelectChange}>
              {list_options.map((options) =>
                <option key={options.id} value={options.name}>{options.name}</option>
              )}
              console.log(list_options)
            </select>{" "}
          </label>{" "}
        </form>{" "}
      </div>
    );
  } else {
    return (
      <div>
        <div classname="toppings">
          Pick your choice here:
          <input
            type="checkbox"
            id="topping"
            name="topping"
            value="One"
            /* checked={isChecked}
            onChange={handleCheckbox} */
          />
          One{" "}
          <input
            type="checkbox"
            id="topping"
            name="topping"
            value="Two"
            /* checked={isChecked}
            onChange={handleCheckbox} */
          />
          Two{" "}
          <input
            type="checkbox"
            id="topping"
            name="topping"
            value="Three"
            /* checked={isChecked}
            onChange={handleCheckbox} */
          />
          Three{" "}
        </div>{" "}
        {/* <div>Above checkbox is {isChecked ? "checked" : "unchecked"}. </div>{" "} */}
      </div>
    );
  }
};

export default DropdownComponent;
