import React, { useState, useCallback } from "react";
import { Button } from "react-bootstrap";
/* import { Dropdown, DropdownButton, DropdownItem } from "react-bootstrap"; */

const DropdownComponent = (props) => {
  const list_options = [
    {
      id: 1,
      name: "One",
    },
    {
      id: 2,
      name: "Two",
    },
    {
      id: 3,
      name: "Three",
    },
    {
      id: 4,
      name: "Four",
    },
    {
      id: 5,
      name: "Five",
    },
  ];
  /* const selectResult = new Array(list_options.length); */
  const { isMulti } = props;
  const [option, setOption] = useState(list_options[0]);
  const [isChecked, setIsChecked] = useState(
    new Array(list_options.length).fill(false)
  );
  const [listSelect, setListSelect] = useState([]);

  const handleSingleSelectChange = useCallback((e) => {
    const selectedChoice = e.target.value;
          setOption(selectedChoice);
    
  }, []);

  const handleMultiSelectChange = useCallback((e) => {
    setIsChecked(!isChecked[(e.target.value)]);
    console.log(e.target);
    if (isChecked[e.target.value]) {
      // setListSelect([...listSelect,e.target.value]);
    }
  }, [isChecked, listSelect]);

  return (
    !isMulti ? (
      <div className="container p-5">
      <select
        className="custom-select"
        value={option}
        onChange={handleSingleSelectChange}
      >
        {list_options.map((options) => (
                <option key={options.id} value={options.name}>
                  {options.name}{" "}
                </option>
                ))}
      </select>
      {option}
    </div>
  ) : (
      <div>
        Pick your choices here:
        {list_options.map((options) => (
          <select key={options.id}>
            <input
              value={options.name}
              onClick={handleMultiSelectChange}
              checked={isChecked[options.id]}
            />
             {options.name}
          </select>
        ))}
        result:
        <div>
          {listSelect.map((choices) => {
            <div>{choices.name}</div>;
          })} 
        </div>
      </div>
    )
  )
};

export default DropdownComponent;
