import React, { useState, useCallback } from "react";
import { Button } from "react-bootstrap";
/* import { Dropdown, DropdownButton, DropdownItem } from "react-bootstrap"; */

const DropdownComponent = (props) => {
  const list_options = [
    {
      id: 1,
      name: "One",
    },
    {
      id: 2,
      name: "Two",
    },
    {
      id: 3,
      name: "Three",
    },
    {
      id: 4,
      name: "Four",
    },
    {
      id: 5,
      name: "Five",
    },
  ];
  /* const selectResult = new Array(list_options.length); */
  const { isMulti } = props;
  const [option, setOption] = useState();//xóa chỗ này pk
  
  const [listSelect, setListSelect] = useState([]);

  const handleSingleSelectChange = useCallback((e) => {
    const selectedChoice = e.target.value;
    setOption(selectedChoice);
  }, []);

  const handleMultiSelectChange = useCallback(
    (e) => {
      const index = listSelect.findIndex((item) => item.id = e.target.value);
      if (index === -1) {
        setListSelect((prev) => [...prev, list_options[index]]);
      }
      console.log(listSelect);
      
    },
    [listSelect]
  );

  return !isMulti ? (
    <div className="container p-5">
      <select
        className="custom-select"
        value={option}
        onChange={handleSingleSelectChange}
      >
        {list_options.map((options) => (
          <option key={options.id} value={options.name}>
            {options.name}
          </option>
        ))}
      </select>
      <br></br>
      Your choice is:  {option}
    </div>
  ) : (
    <div>
      Pick your choices here:
      <div
        className="custom-select"
      >
        {list_options.map((options) => {
          return (
            <>
              <input
              type="checkbox"
              value={options.id}
              onClick={handleMultiSelectChange}
              checked={isChecked[options.id]}
              />
              {options.name}
            </>
          )
        })}
      </div>
      result:
      <div>
        {listSelect.map((choices) => {
          return <div>
            {choices.name}
          </div>;
        })}
      </div>
    </div>
  );
};

export default DropdownComponent;
