import React, { useState, useCallback } from "react";

const DropdownComponent = (props) => {
  const list_options = [
    {
      id: 1,
      name: "One",
      isChecked: false,
    },
    {
      id: 2,
      name: "Two",
      isChecked: false,
    },
    {
      id: 3,
      name: "Three",
      isChecked: false,
    },
    {
      id: 4,
      name: "Four",
      isChecked: false,
    },
    {
      id: 5,
      name: "Five",
    },
  ];
  var selectResult = new Array(list_options.length);
  const { isMulti } = props;
  const [option, selectOption] = useState(list_options[0]);
  const [checkedState, setCheckedState] = useState(
    new Array(list_options.length).fill(false)
  );
  /*  const [selectResult, setSelectResult] = useState(""); */

  const handleMultiSelectChange = (position) => {
    const updatedCheckedState = checkedState.map((item, index) =>
      index === position ? !item : selectResult.push(item)
    );

    setCheckedState(updatedCheckedState);
    
    /* const handleSingleSelectChange = useCallback((e) => {
      selectOption(e.target.value);
    }, []); */

    /* const handleMultiSelectChange = useCallback((itemid) => {
      setIsChecked(!isChecked)
   },) */

    if (isMulti) {
      return (
        <div>
          <form>
            <label>
              Pick your choice here:
              <select value={option} /* onChange={handleSingleSelectChange} */>
                {" "}
                {list_options.map((options) => (
                  <option key={options.id} value={options.name}>
                    {" "}
                    {options.name}{" "}
                  </option>
                ))}{" "}
              </select>{" "}
            </label>{" "}
          </form>{" "}
          <div> Your choice is: {option} </div>{" "}
        </div>
      );
    } else {
      return (
        <div>
          Pick your choices here:{" "}
          {list_options.map((options) => (
            <div key={options.id}>
              <input
                type="checkbox"
                value={options.name}
                check={checkedState[options.id]}
                onChange={handleMultiSelectChange}
              />
              {options.name}
            </div>
          ))}{" "}
          Your choices: {selectResult.map()}
        </div>
      );
    }
  };
};

export default DropdownComponent;
