import React, { useState, useCallback } from "react";
/* import { Dropdown, DropdownButton, DropdownItem } from "react-bootstrap"; */

const DropdownComponent = (props) => {
  const list_options = [
    {
      id: 1,
      name: "One",
    },
    {
      id: 2,
      name: "Two",
    },
    {
      id: 3,
      name: "Three",
    },
    {
      id: 4,
      name: "Four",
    },
    {
      id: 5,
      name: "Five",
    },
  ];
  /* const selectResult = new Array(list_options.length); */
  const { isMulti } = props;
  const [option, selectOption] = useState(list_options[0]);
  const [isChecked, setIsChecked] = useState(new Array());
  isChecked[list_options.length] = false;
  const list_check = new Array();

  const handleSingleSelectChange = useCallback((e) => {
    selectOption(e.target.value);
  }, []);

  const handleMultiSelectChange = useCallback(({index, value}) => {
    setIsChecked(!isChecked[index]);
    if (isChecked[index]) list_check.push(value);
  }, []);

  if (isMulti) {
    return (
      <div>
        <form>
          <label>
            Pick your choice here:
            <select value={option} onChange={handleSingleSelectChange}>
              {" "}
              {list_options.map((options) => (
                <option key={options.id} value={options.name}>
                  {" "}
                  {options.name}{" "}
                </option>
              ))}{" "}
            </select>{" "}
          </label>{" "}
        </form>{" "}
        <div> Your choice is: {option} </div>{" "}
      </div>
    );
  } else {
    return (
      <div>
        Pick your choices here:{" "}
        {list_options.map((options) => (
          <div key={options.id}>
            <input
              type="checkbox"
              value={options.name}
              onClick={handleMultiSelectChange}
              checked={isChecked[options.id]}
            />{" "}
            {options.name}{" "}
          </div>
        ))}
      </div>
    );
  }
};

export default DropdownComponent;
